function roundFrectional(x,n){return Math.round(x*Math.pow(10,n))/Math.pow(10,n);}
$(function(){var b = $('#width'),c = $('#height'),d = $('#calculate'),e = $('#perimeter'),
f = $('#area');d.click(function(){var w  = Number(b.val()),h = Number(c.val());
var p = 2*(roundFrectional(w+h,2 )),a = roundFrectional(w * h,2);e.val(p);
f.val(a);})})